package extraCalisma01;

public class soru07_Extra {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		// bir int degisken alin ve bu degiskeni double byte short float ve long tipinde yazdirin
		
		 int sayi1=15;
		
		System.out.println("int degisken ile :" + sayi1);
		
		short sayi2=(short) sayi1;
		
		System.out.println(sayi2);
		
		double sayi3=sayi1;
		
		System.out.println(sayi3);
		
		float sayi4=sayi1;
		
		System.out.println(sayi4);
		
		long sayi5= sayi1;
		
		System.out.println(sayi5);
		
		byte sayi6=(byte) sayi1;
		
		System.out.println(sayi6);
		
		int sayi7=(int) sayi1;
		
		System.out.println(sayi7);

	}

}
