package extraCalisma03;

public class calsimaSayfasi4 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		String cumle="java ogrenmek cok kolay";
		
		System.out.println(cumle.endsWith("cok kolay"));
		System.out.println(cumle.endsWith("java ogrenmek cok kolay"));
		System.out.println(cumle.endsWith(""));
		
		System.out.println(cumle.startsWith("jav"));
		System.out.println(cumle.startsWith(""));
		System.out.println(cumle.startsWith("java ogrenmek cok kolay"));
	}

}
