package extraCalisma05;

public class Soru14 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		for(int i=5;i>0;i--) {
			for(int j=0;j<i;j++) {
				System.out.print("*");
			}
			System.out.println("");
		}
		
		for(int m=1;m<=5;m++) {
			for(int k=0;k<m;k++) {
				System.out.print("*");
			}
			System.out.println("");
		}

	}

}
